function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	//alert("hello");
	//alert(g_form.getValue('u_request_type'));
if(g_form.getValue('u_request_type') =='aa'){
	g_form.clearOptions('u_what_needed');
	g_form.addOption('u_what_needed','apple1','apple1');
	g_form.addOption('u_what_needed','apple2','apple2');
	g_form.addOption('u_what_needed','apple3','apple3');
}
if(g_form.getValue('u_request_type') =='bb'){g_form.clearOptions('u_what_needed');
	g_form.addOption('u_what_needed','bapple1','bapple1');
	g_form.addOption('u_what_needed','bapple2','bapple2');
	g_form.addOption('u_what_needed','bapple3','bapple3');
}
if(g_form.getValue('u_request_type') =='cc'){g_form.clearOptions('u_what_needed');
	g_form.addOption('u_what_needed','capple1','capple1');
	g_form.addOption('u_what_needed','capple2','capple2');
	g_form.addOption('u_what_needed','capple3','capple3');
}
if(g_form.getValue('u_request_type') =='dd'){g_form.clearOptions('u_what_needed');
	g_form.addOption('u_what_needed','dapple1','dapple1');
	g_form.addOption('u_what_needed','dapple2','dapple2');
	g_form.addOption('u_what_needed','dapple3','dapple3');
}if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_what_needed', 'gdddfgg');
		}
   //Type appropriate comment here, and begin script below

}
